package controller;import com.alibaba.fastjson.JSON;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;@SpringBootApplicationpublic class hello {    public static void main(String[] args) {        SpringApplication.run(hello.class, args);    }}@RestControllerclass HelloController {    @PostMapping("/hello/*")    public String hello(@RequestBody String requestBody) {        JSON.parseObject(requestBody);        return "test";    }}